INSERT INTO reviews (provider_id, author, text, rating, status, created_at) VALUES
(1, 'Дмитрий К.', 'Пользуюсь Timeweb Cloud уже 2 года. Отличная стабильность, серверы практически не падают. Поддержка быстро отвечает, хотя иногда приходится ждать. Цены адекватные, особенно с учетом качества.', 5, 'approved', NOW() - INTERVAL '45 days'),
(1, 'Анна М.', 'Перешла с другого хостинга на Timeweb и очень довольна. Панель управления интуитивно понятная, даже для новичка. Скорость загрузки сайта заметно выросла. Рекомендую!', 5, 'approved', NOW() - INTERVAL '38 days'),
(1, 'Сергей П.', 'Неплохой хостинг, но были проблемы с миграцией. Техподдержка помогла, но пришлось ждать около суток. В остальном всё работает стабильно, придраться не к чему.', 4, 'approved', NOW() - INTERVAL '32 days'),
(1, 'Екатерина Л.', 'Отличное соотношение цена-качество! Для моего интернет-магазина на WooCommerce это идеальный вариант. Скорость отличная, бэкапы делаются автоматически.', 5, 'approved', NOW() - INTERVAL '27 days'),
(1, 'Алексей В.', 'Пользуюсь несколько месяцев. В целом доволен, но один раз был непонятный даунтайм на 15 минут. Поддержка объяснила, что была плановая работа, но не предупредили заранее. Минус за это.', 4, 'approved', NOW() - INTERVAL '21 days'),
(1, 'Мария С.', 'Лучший хостинг, который у меня был! Быстрая регистрация, простая настройка SSL, автоматические обновления. Поддержка вежливая и компетентная. Всем советую!', 5, 'approved', NOW() - INTERVAL '18 days'),
(1, 'Игорь Н.', 'Использую для нескольких проектов на Django. Всё работает быстро, ресурсов хватает с запасом. Удобно масштабировать конфигурацию прямо из панели. Рекомендую разработчикам.', 5, 'approved', NOW() - INTERVAL '14 days'),
(1, 'Ольга Т.', 'Неплохо, но цены чуть выше, чем у конкурентов. Зато качество на высоте, серверы шустрые, аптайм почти 100%. Для серьёзных проектов подходит отлично.', 4, 'approved', NOW() - INTERVAL '10 days'),
(1, 'Павел Ж.', 'Раньше был на shared-хостинге, перешёл на VDS в Timeweb — небо и земля! Сайт летает, никаких тормозов. Немного сложновато разобраться сначала, но есть хорошая документация.', 5, 'approved', NOW() - INTERVAL '7 days'),
(1, 'Наталья Р.', 'Пользуюсь год, всё устраивает. Один раз обращалась в поддержку — ответили за 10 минут и всё решили. Цены адекватные, качество хорошее. Буду продолжать использовать.', 5, 'approved', NOW() - INTERVAL '4 days'),
(1, 'Владимир К.', 'Хороший провайдер для малого и среднего бизнеса. Использую для корпоративного сайта и CRM. Стабильно работает, проблем не было. Единственное — хотелось бы больше дата-центров в регионах.', 4, 'approved', NOW() - INTERVAL '2 days');
