INSERT INTO reviews (provider_id, author, text, rating, status, created_at) VALUES
(2, 'Анна Ковалева', 'Пользуюсь Beget уже 3 года для личного блога. Цена просто смешная - 150 рублей в месяц! Панель управления очень простая, разобралась за час. Автобэкапы несколько раз спасали от моих же ошибок. Для новичков - идеальный вариант.', 5, 'approved', NOW() - INTERVAL '42 days'),
(2, 'Максим Петров', 'Перешёл с другого хостинга на Beget ради цены. За свои деньги - отлично! Правда, когда трафик вырос, сайт начал подтормаживать. Пришлось апгрейдить тариф. Техподдержка отвечала часа 3, но помогли разобраться.', 4, 'approved', NOW() - INTERVAL '35 days'),
(2, 'Елена Смирнова', 'Отличный хостинг для старта! За 150₽ получаешь всё необходимое: SSL бесплатный, автобэкапы, защиту от DDoS. Установила WordPress одним кликом - готовых образов очень много. Новичкам советую однозначно!', 5, 'approved', NOW() - INTERVAL '29 days'),
(2, 'Дмитрий Волков', 'Использую для небольшого интернет-магазина. В целом доволен, но при скачках посещаемости сайт начинает тормозить. Техподдержка работает медленно - ответа ждал 4 часа. Но за такую цену терпимо.', 3, 'approved', NOW() - INTERVAL '26 days'),
(2, 'Ирина Соколова', 'Панель управления очень простая и понятная! Я вообще не программист, но смогла сама установить и настроить сайт на Joomla. Автобэкапы работают отлично - один раз случайно удалила файлы, восстановили за 5 минут!', 5, 'approved', NOW() - INTERVAL '22 days'),
(2, 'Алексей Морозов', 'Цена - главное преимущество Beget. За 150 рублей сложно найти что-то лучше. Минусы: нет выделенного IP в базовом тарифе, производительность ограничена. Для блога или визитки - самое то!', 4, 'approved', NOW() - INTERVAL '19 days'),
(2, 'Ольга Новикова', 'Готовых CMS больше 100 штук - выбрала PrestaShop для магазина. Установка прошла автоматически за минуту! SSL сертификат бесплатный - не нужно покупать отдельно. За свои деньги - просто находка!', 5, 'approved', NOW() - INTERVAL '16 days'),
(2, 'Сергей Лебедев', 'Техподдержка отвечает очень долго - приходится ждать по несколько часов. Но когда отвечают, помогают разобраться. Производительность для высоконагруженных проектов слабовата. Для старта сойдёт.', 3, 'approved', NOW() - INTERVAL '13 days'),
(2, 'Марина Козлова', 'Лучший хостинг для новичков! Всё интуитивно понятно, инструкций куча. Поставила сайт на Битриксе за 10 минут вместе с настройкой. Автобэкапы - просто спасение! Цена копеечная!', 5, 'approved', NOW() - INTERVAL '11 days'),
(2, 'Павел Иванов', 'За 150₽ в месяц - отличный вариант для портфолио или лендинга. Но для интернет-магазина с большим трафиком уже маловато. Сайт периодически подвисает. Придётся переходить на более мощный хостинг.', 3, 'approved', NOW() - INTERVAL '8 days'),
(2, 'Татьяна Федорова', 'SSL бесплатно, автобэкапы включены, защита от DDoS работает - за такую цену это просто подарок! Пользуюсь уже год, ни разу не пожалела о выборе. Для небольших проектов идеально!', 5, 'approved', NOW() - INTERVAL '6 days'),
(2, 'Андрей Николаев', 'Производительность слабовата - когда запустил рекламу, сайт начал сильно тормозить. Пришлось докупать ресурсы. Техподдержка помогла оптимизировать, но ответа ждал долго. В целом за свои деньги нормально.', 4, 'approved', NOW() - INTERVAL '4 days'),
(2, 'Светлана Егорова', 'Панель управления простая даже для чайника! Я никогда не имела дела с хостингами, но всё поняла интуитивно. Готовые CMS - это вообще находка! Выбрала Drupal, установилась за минуту. Супер!', 5, 'approved', NOW() - INTERVAL '3 days'),
(2, 'Игорь Семенов', 'Использую для нескольких лендингов. За свою цену - отличный вариант. Минусы: нет выделенного IP, техподдержка медленная. Но для некритичных проектов вполне подходит. Автобэкапы спасали не раз!', 4, 'approved', NOW() - INTERVAL '2 days'),
(2, 'Наталья Григорьева', 'Самый дешёвый хостинг, который я нашла! За 150 рублей получаешь всё необходимое для старта. Правда, для больших проектов не подойдёт - производительности не хватит. Но для начала - идеально!', 5, 'approved', NOW() - INTERVAL '1 day');
